pragma solidity >=0.4.21 <0.7.0;

import "./ERC165.sol";

contract ERC165Mappable is ERC165 {
    /// @dev You must not set element 0xffffffff to true
    mapping(bytes4 => bool) internal supportedInterfaces;

    function ERC165MappingImplementation() internal {
        supportedInterfaces[this.supportsInterface.selector] = true;
    }

    function supportsInterface(bytes4 interfaceID) external view returns (bool) {
        return supportedInterfaces[interfaceID];
    }
}

interface AmazingRace {
  function register() external payable;
  function prove(string calldata guess) external returns (bool);
  function gimmie(string calldata thing) external pure returns (bytes32);
}

contract mvp is AmazingRace, ERC165Mappable {

  bytes32 constant secretHash = 0x419a4502e8d11c5715b5c9a8f63092f3b738640922cecce22029174bce39ec3f;
  function () external payable {
    register();
  }

  function register() public payable {
    // kiss
    require (msg.value == 1 ether, "Register with exactly 1 ether");
  }

  function gimmie(string memory thing) public pure returns (bytes32 resp) {
    return keccak256(abi.encodePacked(thing));
  }

  function prove(string memory guess) public returns (bool) {
    // let blah = await mvp.deployed()
    //blah.prove.call("pacific city", {from: accounts[0]});
    return keccak256(abi.encodePacked(guess)) == secretHash;
  }

}
